"use client";

import {
  useRadioGroup,
  type UseRadioGroupProps,
  type RadioItemProps,
} from "@seed-design/react-radio-group";
import {
  useCheckbox,
  type UseCheckboxProps,
} from "@seed-design/react-checkbox";
import {
  type SelectBoxGroupVariantProps,
  selectBoxGroup,
} from "@seed-design/recipe/selectBoxGroup";
import { IconCheckmarkFatFill } from "@daangn/react-monochrome-icon";
import clsx from "clsx";
import * as React from "react";

import type { Assign } from "../util/types";
import { visuallyHidden } from "../util/visuallyHidden";

import "@seed-design/stylesheet/selectBoxGroup.css";

const RadioSelectBoxGroupContext = React.createContext<{
  api: ReturnType<typeof useRadioGroup>;
} | null>(null);

const useSelectBoxContext = () => {
  const context = React.useContext(RadioSelectBoxGroupContext);
  if (!context)
    throw new Error(
      "RadioSelectBox cannot be rendered outside the RadioSelectBoxGroup",
    );

  return context;
};

export interface RadioSelectBoxGroupProps
  extends Assign<React.HTMLAttributes<HTMLDivElement>, UseRadioGroupProps> {}

export const RadioSelectBoxGroup = React.forwardRef<
  HTMLDivElement,
  RadioSelectBoxGroupProps
>(({ className, children, ...otherProps }, ref) => {
  const api = useRadioGroup(otherProps);
  const {
    rootProps: { "aria-labelledby": _, ...rootProps },
    restProps,
  } = api;

  const classNames = selectBoxGroup();

  return (
    <div
      ref={ref}
      {...rootProps}
      {...restProps}
      className={clsx(classNames.root, className)}
    >
      {/* <div {...labelProps}>
        {label}
      </div> */}
      <RadioSelectBoxGroupContext.Provider value={{ api }}>
        {children}
      </RadioSelectBoxGroupContext.Provider>
    </div>
  );
});
RadioSelectBoxGroup.displayName = "RadioSelectBoxGroup";

export interface RadioSelectBoxProps
  extends SelectBoxGroupVariantProps,
    Assign<
      Omit<React.HTMLAttributes<HTMLInputElement>, "children">,
      RadioItemProps
    > {
  label: string;
  description?: string;
}

export const RadioSelectBox = React.forwardRef<
  HTMLInputElement,
  RadioSelectBoxProps
>(({ className, label, description, ...otherProps }, ref) => {
  const { api } = useSelectBoxContext();
  const { getItemProps } = api;
  const { rootProps, restProps, hiddenInputProps, controlProps, stateProps } =
    getItemProps(otherProps);

  const classNames = selectBoxGroup();

  return (
    <label
      className={clsx(classNames.box, className)}
      {...rootProps}
      {...restProps}
    >
      <input ref={ref} {...hiddenInputProps} style={visuallyHidden} />
      <div className={classNames.content}>
        <span className={classNames.label}>{label}</span>
        {description && (
          <span className={classNames.description}>{description}</span>
        )}
      </div>
      <div {...controlProps} className={classNames.radioControl}>
        <div {...stateProps} className={classNames.radioIcon} />
      </div>
    </label>
  );
});
RadioSelectBox.displayName = "RadioSelectBox";

export interface CheckSelectBoxGroupProps
  extends React.HTMLAttributes<HTMLDivElement> {}

export const CheckSelectBoxGroup = React.forwardRef<
  HTMLDivElement,
  CheckSelectBoxGroupProps
>(({ className, children, ...otherProps }, ref) => {
  const classNames = selectBoxGroup();

  return (
    <div ref={ref} {...otherProps} className={clsx(classNames.root, className)}>
      {children}
    </div>
  );
});
CheckSelectBoxGroup.displayName = "CheckSelectBoxGroup";

export interface CheckSelectBoxProps
  extends SelectBoxGroupVariantProps,
    Assign<
      Omit<React.InputHTMLAttributes<HTMLInputElement>, "children">,
      UseCheckboxProps
    > {
  label: string;
  description?: string;
}

export const CheckSelectBox = React.forwardRef<
  HTMLInputElement,
  CheckSelectBoxProps
>(({ className, label, description, ...otherProps }, ref) => {
  const { rootProps, restProps, hiddenInputProps, controlProps, stateProps } =
    useCheckbox(otherProps);

  const classNames = selectBoxGroup();

  return (
    <label
      className={clsx(classNames.box, className)}
      {...rootProps}
      {...restProps}
    >
      <input ref={ref} {...hiddenInputProps} style={visuallyHidden} />
      <div className={classNames.content}>
        <span className={classNames.label}>{label}</span>
        {description && (
          <span className={classNames.description}>{description}</span>
        )}
      </div>
      <div {...controlProps} className={classNames.checkboxControl}>
        <IconCheckmarkFatFill
          aria-hidden
          {...stateProps}
          className={classNames.checkboxIcon}
        />
      </div>
    </label>
  );
});
CheckSelectBox.displayName = "CheckSelectBox";

import type { CSSProperties } from "react";

export const visuallyHidden: CSSProperties = {
  border: 0,
  clip: "rect(0 0 0 0)",
  height: "1px",
  margin: "-1px",
  overflow: "hidden",
  padding: 0,
  position: "absolute",
  whiteSpace: "nowrap",
  width: "1px",
};
